import { useEffect, useMemo, useState } from 'react';

import { Search, ShoppingBagOutlined } from '@mui/icons-material';
import {
  Box,
  Card,
  CardActionArea,
  CardContent,
  CardMedia,
  Typography,
} from '@mui/material';

import { api } from '../../services/api';
import { Category } from '../../types/category';

import Navbar from '../../components/Navbar';
import Carousel from '../../components/Carousel';

import * as S from './styles';

const Home = () => {
  const [categories, setCategories] = useState<Category[] | any>();

  // const getProducts = async () => {
  //   try {
  //     const response = await api.get('products');
  //     // console.log('PRODUTOS', response.data);
  //   } catch (error) {
  //     console.log('ERROR:', error);
  //   }
  // };

  const getCategories = async () => {
    try {
      const response = await api.get(`categories?limit=5`);
      setCategories(response.data);
    } catch (error) {
      console.log('ERROR:', error);
    }
  };

  useEffect(() => {
    // getProducts();
    getCategories();
  }, []);

  const limitedOptions = useMemo(() => {
    if (categories) {
      const teste: Category[] = [];
      for (let index = 0; index < 5; index++) {
        const randomIndex = Math.floor(Math.random() * categories.length);

        teste.push(categories[randomIndex]);
      }
      return teste;
    }
  }, [categories]);

  console.log(categories);

  return (
    <>
      <S.Container>
        <S.BoxAlign>
          <Typography variant="h4">Logo</Typography>
        </S.BoxAlign>

        <S.ContainerSearch>
          <S.BoxSearch>
            <S.BoxSearchIcon>
              <Search style={{ color: '#ffffff' }} />
            </S.BoxSearchIcon>
          </S.BoxSearch>
        </S.ContainerSearch>

        <S.BoxAlign>
          <ShoppingBagOutlined />
        </S.BoxAlign>
      </S.Container>

      <S.ContainerNavbar display="flex" justifyContent="center">
        <Box display="flex" width="70%" justifyContent="space-between">
          <Navbar options={categories} />
        </Box>
      </S.ContainerNavbar>

      <S.ContainerCarousel>
        <S.BoxCarousel>
          <S.WidthCarousel>
            <Carousel categories={categories} />
          </S.WidthCarousel>
        </S.BoxCarousel>
      </S.ContainerCarousel>
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
        }}
      >
        <Box
          sx={{
            width: '70%',
            flexDirection: 'column',
          }}
        >
          <Typography
            display="flex"
            justifyContent="center"
            my={5}
            variant="h4"
          >
            Categorias
          </Typography>

          <Box
            sx={{
              width: '100%',
              display: 'flex',
              justifyContent: 'space-between',
              flexDirection: 'row',
              flexWrap: 'wrap',
              border: '1px solid red',
            }}
          >
            {categories?.map((item: any) => {
              return (
                <Card
                  sx={{
                    width: 100,
                    border: '1px solid red',
                    borderRadius: '100%',
                  }}
                >
                  <CardActionArea>
                    <CardMedia
                      component="img"
                      height="100"
                      image={item.image}
                      alt={item.name}
                    />
                    {/* <CardContent>
                      <Typography gutterBottom variant="h5" component="div">
                        {item.name}
                      </Typography>
                    </CardContent> */}
                  </CardActionArea>
                </Card>
              );
            })}
          </Box>
        </Box>
      </Box>
    </>
  );
};

export default Home;
